function Colaboradores_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Colaboradores');
	if(!$arrPerm['insert']) return false;

	$data = [
		'Nome' => Request::lookup('Nome', ''),
		'Bairro' => Request::lookup('Nome'),
		'Profissao' => Request::lookup('Profissao', ''),
		'Funcao' => Request::lookup('Funcao', ''),
		'Responsavel' => Request::lookup('Responsavel', ''),
		'Obs' => br2nl(Request::val('Obs', '')),
	];

	if($data['Nome'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Nome': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['Funcao'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Fun&#231;&#227;o': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}

	// hook: Colaboradores_before_insert
	if(function_exists('Colaboradores_before_insert')) {
		$args = [];
		if(!Colaboradores_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Colaboradores', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('Colaboradores', $recID, calculated_fields()['Colaboradores']);

	// hook: Colaboradores_after_insert
	if(function_exists('Colaboradores_after_insert')) {
		$res = sql("SELECT * FROM `Colaboradores` WHERE `Id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!Colaboradores_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	// record owner is current user
	$recordOwner = getLoggedMemberID();
	set_record_owner('Colaboradores', $recID, $recordOwner);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) Colaboradores_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

