	<?php

	$lookups = str_replace('__RAND__', $rnd1, ob_get_clean());


	// code for template based detail view forms

	// open the detail view template
	if($dvprint) {
		$template_file = is_file("./{$templateDVP}") ? "./{$templateDVP}" : './templates/Colaboradores_templateDVP.html';
		$templateCode = @file_get_contents($template_file);
	} else {
		$template_file = is_file("./{$templateDV}") ? "./{$templateDV}" : './templates/Colaboradores_templateDV.html';
		$templateCode = @file_get_contents($template_file);
	}

	// process form title
	$templateCode = str_replace('<%%DETAIL_VIEW_TITLE%%>', 'Detalhes', $templateCode);
	$templateCode = str_replace('<%%RND1%%>', $rnd1, $templateCode);
	$templateCode = str_replace('<%%EMBEDDED%%>', (Request::val('Embedded') ? 'Embedded=1' : ''), $templateCode);
	// process buttons
	if($showSaveNew) {
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-success" id="insert" name="insert_x" value="1" onclick="return Colaboradores_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save New'] . '</button>', $templateCode);
	} elseif($showSaveAsCopy) {
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="insert" name="insert_x" value="1" onclick="return Colaboradores_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save As Copy'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '', $templateCode);
	}

	// 'Back' button action
	if(Request::val('Embedded')) {
		$backAction = 'AppGini.closeParentModal(); return false;';
	} else {
		$backAction = '$j(\'form\').eq(0).attr(\'novalidate\', \'novalidate\'); document.myform.reset(); return true;';
	}

	if($hasSelectedId) {
		if(!Request::val('Embedded')) $templateCode = str_replace('<%%DVPRINT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="dvprint" name="dvprint_x" value="1" onclick="$j(\'form\').eq(0).prop(\'novalidate\', true); document.myform.reset(); return true;" title="' . html_attr($Translation['Print Preview']) . '"><i class="glyphicon glyphicon-print"></i> ' . $Translation['Print Preview'] . '</button>', $templateCode);
		if($allowUpdate)
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '<button type="submit" class="btn btn-success btn-lg" id="update" name="update_x" value="1" onclick="return Colaboradores_validateData();" title="' . html_attr($Translation['Save Changes']) . '"><i class="glyphicon glyphicon-ok"></i> ' . $Translation['Save Changes'] . '</button>', $templateCode);
		else
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);

		if($allowDelete)
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		else
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);

		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);

		// if not in embedded mode and user has insert only but no view/update/delete,
		// remove 'back' button
		if(
			$allowInsert
			&& !$allowUpdate && !$allowDelete && !$arrPerm['view']
			&& !Request::val('Embedded')
		)
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
		elseif($separateDV)
			$templateCode = str_replace(
				'<%%DESELECT_BUTTON%%>', 
				'<button
					type="submit" 
					class="btn btn-default" 
					id="deselect" 
					name="deselect_x" 
					value="1" 
					onclick="' . $backAction . '" 
					title="' . html_attr($Translation['Back']) . '">
						<i class="glyphicon glyphicon-chevron-left"></i> ' .
						$Translation['Back'] .
				'</button>',
				$templateCode
			);
		else
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(!$fieldsAreEditable) {
		$jsReadOnly = '';
		$jsReadOnly .= "\tjQuery('#Nome').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#Nome_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#Profissao').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#Profissao_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#Funcao').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#Funcao_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#Responsavel').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#Responsavel_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#Obs').replaceWith('<div class=\"form-control-static\" id=\"Obs\">' + (jQuery('#Obs').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} else {
		// temporarily disable form change handler till time and datetime pickers are enabled
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);";
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(Nome)%%>', $combo_Nome->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(Nome)%%>', $combo_Nome->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(Nome)%%>', urlencode($combo_Nome->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(Profissao)%%>', $combo_Profissao->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(Profissao)%%>', $combo_Profissao->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(Profissao)%%>', urlencode($combo_Profissao->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(Funcao)%%>', $combo_Funcao->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(Funcao)%%>', $combo_Funcao->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(Funcao)%%>', urlencode($combo_Funcao->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(Responsavel)%%>', $combo_Responsavel->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(Responsavel)%%>', $combo_Responsavel->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(Responsavel)%%>', urlencode($combo_Responsavel->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => ['parent table name', 'lookup field caption'] */
	$lookup_fields = ['Nome' => ['Pessoas', 'Nome'], 'Profissao' => ['Profissoes', 'Profiss&#227;o'], 'Funcao' => ['Funcoes', 'Fun&#231;&#227;o'], 'Responsavel' => ['Colaboradores', 'Responsavel'], ];
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if(($pt_perm['view'] && isDetailViewEnabled($ptfc[0])) || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !Request::val('Embedded')*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-default add_new_parent" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus text-success"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(Id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Nome)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Profissao)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Funcao)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Responsavel)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Obs)%%>', '', $templateCode);

	// process values
	if($hasSelectedId) {
		$templateCode = str_replace('<%%VALUE(Id)%%>', safe_html($urow['Id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Id)%%>', urlencode($urow['Id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Nome)%%>', safe_html($urow['Nome']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Nome)%%>', html_attr($row['Nome']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Nome)%%>', urlencode($urow['Nome']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Profissao)%%>', safe_html($urow['Profissao']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Profissao)%%>', html_attr($row['Profissao']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Profissao)%%>', urlencode($urow['Profissao']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Funcao)%%>', safe_html($urow['Funcao']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Funcao)%%>', html_attr($row['Funcao']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Funcao)%%>', urlencode($urow['Funcao']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Responsavel)%%>', safe_html($urow['Responsavel']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Responsavel)%%>', html_attr($row['Responsavel']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Responsavel)%%>', urlencode($urow['Responsavel']), $templateCode);
		$templateCode = str_replace('<%%VALUE(Obs)%%>', safe_html($urow['Obs'], $fieldsAreEditable), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Obs)%%>', urlencode($urow['Obs']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(Id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Nome)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Nome)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Profissao)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Profissao)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Funcao)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Funcao)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Responsavel)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Responsavel)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Obs)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Obs)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$hasSelectedId) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';

	$templateCode .= "\tNome_update_autofills$rnd1 = function() {\n";
	$templateCode .= "\t\t\$j.ajax({\n";
	if($dvprint) {
		$templateCode .= "\t\t\turl: 'Colaboradores_autofill.php?rnd1=$rnd1&mfk=Nome&id=' + encodeURIComponent('".addslashes($row['Nome'])."'),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET'\n";
	} else {
		$templateCode .= "\t\t\turl: 'Colaboradores_autofill.php?rnd1=$rnd1&mfk=Nome&id=' + encodeURIComponent(AppGini.current_Nome{$rnd1}.value),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET',\n";
		$templateCode .= "\t\t\tbeforeSend: function() { \$j('#Nome$rnd1').prop('disabled', true); },\n";
		$templateCode .= "\t\t\tcomplete: function() { " . (($allowInsert || $allowUpdate) ? "\$j('#Nome$rnd1').prop('disabled', false); " : "\$j('#Nome$rnd1').prop('disabled', true); ")." \$j(window).resize(); }\n";
	}
	$templateCode .= "\t\t});\n";
	$templateCode .= "\t};\n";
	if(!$dvprint) $templateCode .= "\tif(\$j('#Nome_caption').length) \$j('#Nome_caption').click(function() { Nome_update_autofills$rnd1(); });\n";


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Colaboradores');
	if($hasSelectedId) {
		$jdata = get_joined_record('Colaboradores', $selectedId);
		if($jdata === false) $jdata = get_defaults('Colaboradores');
		$rdata = $row;
	}
	$templateCode .= loadView('Colaboradores-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: Colaboradores_dv
	if(function_exists('Colaboradores_dv')) {
		$args = [];
		Colaboradores_dv(($hasSelectedId ? $selectedId : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}