function Eleitores_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Eleitores');
	if(!$arrPerm['insert']) return false;

	$data = [
		'Nome' => Request::lookup('Nome', ''),
		'Mae' => Request::val('Mae', ''),
		'DataN' => Request::dateComponents('DataN', ''),
		'CPF' => Request::val('CPF', ''),
		'Titulo' => Request::val('Titulo', ''),
		'Estado' => Request::lookup('Nome'),
		'Cidade' => Request::lookup('Nome'),
		'Bairro' => Request::lookup('Nome'),
		'Zona' => Request::lookup('Zona', ''),
		'secao' => Request::lookup('secao', ''),
		'local' => Request::lookup('secao'),
		'partido' => Request::lookup('partido', ''),
		'indicacao' => Request::lookup('indicacao', ''),
	];

	if($data['Nome'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Nome': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}

	// hook: Eleitores_before_insert
	if(function_exists('Eleitores_before_insert')) {
		$args = [];
		if(!Eleitores_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Eleitores', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('Eleitores', $recID, calculated_fields()['Eleitores']);

	// hook: Eleitores_after_insert
	if(function_exists('Eleitores_after_insert')) {
		$res = sql("SELECT * FROM `Eleitores` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!Eleitores_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	// record owner is current user
	$recordOwner = getLoggedMemberID();
	set_record_owner('Eleitores', $recID, $recordOwner);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) Eleitores_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

