	<?php

	$lookups = str_replace('__RAND__', $rnd1, ob_get_clean());


	// code for template based detail view forms

	// open the detail view template
	if($dvprint) {
		$template_file = is_file("./{$templateDVP}") ? "./{$templateDVP}" : './templates/Eleitores_templateDVP.html';
		$templateCode = @file_get_contents($template_file);
	} else {
		$template_file = is_file("./{$templateDV}") ? "./{$templateDV}" : './templates/Eleitores_templateDV.html';
		$templateCode = @file_get_contents($template_file);
	}

	// process form title
	$templateCode = str_replace('<%%DETAIL_VIEW_TITLE%%>', 'Detalhes', $templateCode);
	$templateCode = str_replace('<%%RND1%%>', $rnd1, $templateCode);
	$templateCode = str_replace('<%%EMBEDDED%%>', (Request::val('Embedded') ? 'Embedded=1' : ''), $templateCode);
	// process buttons
	if($showSaveNew) {
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-success" id="insert" name="insert_x" value="1" onclick="return Eleitores_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save New'] . '</button>', $templateCode);
	} elseif($showSaveAsCopy) {
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="insert" name="insert_x" value="1" onclick="return Eleitores_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save As Copy'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '', $templateCode);
	}

	// 'Back' button action
	if(Request::val('Embedded')) {
		$backAction = 'AppGini.closeParentModal(); return false;';
	} else {
		$backAction = '$j(\'form\').eq(0).attr(\'novalidate\', \'novalidate\'); document.myform.reset(); return true;';
	}

	if($hasSelectedId) {
		if(!Request::val('Embedded')) $templateCode = str_replace('<%%DVPRINT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="dvprint" name="dvprint_x" value="1" onclick="$j(\'form\').eq(0).prop(\'novalidate\', true); document.myform.reset(); return true;" title="' . html_attr($Translation['Print Preview']) . '"><i class="glyphicon glyphicon-print"></i> ' . $Translation['Print Preview'] . '</button>', $templateCode);
		if($allowUpdate)
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '<button type="submit" class="btn btn-success btn-lg" id="update" name="update_x" value="1" onclick="return Eleitores_validateData();" title="' . html_attr($Translation['Save Changes']) . '"><i class="glyphicon glyphicon-ok"></i> ' . $Translation['Save Changes'] . '</button>', $templateCode);
		else
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);

		if($allowDelete)
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		else
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);

		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);

		// if not in embedded mode and user has insert only but no view/update/delete,
		// remove 'back' button
		if(
			$allowInsert
			&& !$allowUpdate && !$allowDelete && !$arrPerm['view']
			&& !Request::val('Embedded')
		)
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
		elseif($separateDV)
			$templateCode = str_replace(
				'<%%DESELECT_BUTTON%%>', 
				'<button
					type="submit" 
					class="btn btn-default" 
					id="deselect" 
					name="deselect_x" 
					value="1" 
					onclick="' . $backAction . '" 
					title="' . html_attr($Translation['Back']) . '">
						<i class="glyphicon glyphicon-chevron-left"></i> ' .
						$Translation['Back'] .
				'</button>',
				$templateCode
			);
		else
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(!$fieldsAreEditable) {
		$jsReadOnly = '';
		$jsReadOnly .= "\tjQuery('#Nome').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#Nome_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#Mae').replaceWith('<div class=\"form-control-static\" id=\"Mae\">' + (jQuery('#Mae').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#DataN').prop('readonly', true);\n";
		$jsReadOnly .= "\tjQuery('#DataNDay, #DataNMonth, #DataNYear').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#CPF').replaceWith('<div class=\"form-control-static\" id=\"CPF\">' + (jQuery('#CPF').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#Titulo').replaceWith('<div class=\"form-control-static\" id=\"Titulo\">' + (jQuery('#Titulo').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#Zona').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#Zona_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#secao').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#secao_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#partido').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#partido_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#indicacao').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#indicacao_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} else {
		// temporarily disable form change handler till time and datetime pickers are enabled
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);";
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(Nome)%%>', $combo_Nome->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(Nome)%%>', $combo_Nome->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(Nome)%%>', urlencode($combo_Nome->MatchText), $templateCode);
	$templateCode = str_replace(
		'<%%COMBO(DataN)%%>', 
		(!$fieldsAreEditable ? 
			'<div class="form-control-static">' . $combo_DataN->GetHTML(true) . '</div>' : 
			$combo_DataN->GetHTML()
		), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(DataN)%%>', $combo_DataN->GetHTML(true), $templateCode);
	$templateCode = str_replace('<%%COMBO(Zona)%%>', $combo_Zona->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(Zona)%%>', $combo_Zona->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(Zona)%%>', urlencode($combo_Zona->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(secao)%%>', $combo_secao->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(secao)%%>', $combo_secao->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(secao)%%>', urlencode($combo_secao->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(partido)%%>', $combo_partido->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(partido)%%>', $combo_partido->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(partido)%%>', urlencode($combo_partido->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(indicacao)%%>', $combo_indicacao->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(indicacao)%%>', $combo_indicacao->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(indicacao)%%>', urlencode($combo_indicacao->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => ['parent table name', 'lookup field caption'] */
	$lookup_fields = ['Nome' => ['Pessoas', 'Nome'], 'Zona' => ['Zona', 'Zona'], 'secao' => ['Secao', 'Se&#231;&#227;o'], 'partido' => ['Partidos', 'Partido'], 'indicacao' => ['Colaboradores', 'Indica&#231;&#227;o'], ];
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if(($pt_perm['view'] && isDetailViewEnabled($ptfc[0])) || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !Request::val('Embedded')*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-default add_new_parent" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus text-success"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Nome)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Mae)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(DataN)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(CPF)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Titulo)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Zona)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(secao)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(partido)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(indicacao)%%>', '', $templateCode);

	// process values
	if($hasSelectedId) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Nome)%%>', safe_html($urow['Nome']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Nome)%%>', html_attr($row['Nome']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Nome)%%>', urlencode($urow['Nome']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Mae)%%>', safe_html($urow['Mae']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Mae)%%>', html_attr($row['Mae']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Mae)%%>', urlencode($urow['Mae']), $templateCode);
		$templateCode = str_replace('<%%VALUE(DataN)%%>', app_datetime($row['DataN']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(DataN)%%>', urlencode(app_datetime($urow['DataN'])), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(CPF)%%>', safe_html($urow['CPF']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(CPF)%%>', html_attr($row['CPF']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(CPF)%%>', urlencode($urow['CPF']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Titulo)%%>', safe_html($urow['Titulo']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Titulo)%%>', html_attr($row['Titulo']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Titulo)%%>', urlencode($urow['Titulo']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Zona)%%>', safe_html($urow['Zona']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Zona)%%>', html_attr($row['Zona']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Zona)%%>', urlencode($urow['Zona']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(secao)%%>', safe_html($urow['secao']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(secao)%%>', html_attr($row['secao']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(secao)%%>', urlencode($urow['secao']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(partido)%%>', safe_html($urow['partido']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(partido)%%>', html_attr($row['partido']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(partido)%%>', urlencode($urow['partido']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(indicacao)%%>', safe_html($urow['indicacao']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(indicacao)%%>', html_attr($row['indicacao']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(indicacao)%%>', urlencode($urow['indicacao']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Nome)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Nome)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Mae)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Mae)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(DataN)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(DataN)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(CPF)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(CPF)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Titulo)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Titulo)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Zona)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Zona)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(secao)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(secao)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(partido)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(partido)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(indicacao)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(indicacao)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$hasSelectedId) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';

	$templateCode .= "\tNome_update_autofills$rnd1 = function() {\n";
	$templateCode .= "\t\t\$j.ajax({\n";
	if($dvprint) {
		$templateCode .= "\t\t\turl: 'Eleitores_autofill.php?rnd1=$rnd1&mfk=Nome&id=' + encodeURIComponent('".addslashes($row['Nome'])."'),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET'\n";
	} else {
		$templateCode .= "\t\t\turl: 'Eleitores_autofill.php?rnd1=$rnd1&mfk=Nome&id=' + encodeURIComponent(AppGini.current_Nome{$rnd1}.value),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET',\n";
		$templateCode .= "\t\t\tbeforeSend: function() { \$j('#Nome$rnd1').prop('disabled', true); },\n";
		$templateCode .= "\t\t\tcomplete: function() { " . (($allowInsert || $allowUpdate) ? "\$j('#Nome$rnd1').prop('disabled', false); " : "\$j('#Nome$rnd1').prop('disabled', true); ")." \$j(window).resize(); }\n";
	}
	$templateCode .= "\t\t});\n";
	$templateCode .= "\t};\n";
	if(!$dvprint) $templateCode .= "\tif(\$j('#Nome_caption').length) \$j('#Nome_caption').click(function() { Nome_update_autofills$rnd1(); });\n";

	$templateCode .= "\tsecao_update_autofills$rnd1 = function() {\n";
	$templateCode .= "\t\t\$j.ajax({\n";
	if($dvprint) {
		$templateCode .= "\t\t\turl: 'Eleitores_autofill.php?rnd1=$rnd1&mfk=secao&id=' + encodeURIComponent('".addslashes($row['secao'])."'),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET'\n";
	} else {
		$templateCode .= "\t\t\turl: 'Eleitores_autofill.php?rnd1=$rnd1&mfk=secao&id=' + encodeURIComponent(AppGini.current_secao{$rnd1}.value),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET',\n";
		$templateCode .= "\t\t\tbeforeSend: function() { \$j('#secao$rnd1').prop('disabled', true); },\n";
		$templateCode .= "\t\t\tcomplete: function() { " . (($allowInsert || $allowUpdate) ? "\$j('#secao$rnd1').prop('disabled', false); " : "\$j('#secao$rnd1').prop('disabled', true); ")." \$j(window).resize(); }\n";
	}
	$templateCode .= "\t\t});\n";
	$templateCode .= "\t};\n";
	if(!$dvprint) $templateCode .= "\tif(\$j('#secao_caption').length) \$j('#secao_caption').click(function() { secao_update_autofills$rnd1(); });\n";


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Eleitores');
	if($hasSelectedId) {
		$jdata = get_joined_record('Eleitores', $selectedId);
		if($jdata === false) $jdata = get_defaults('Eleitores');
		$rdata = $row;
	}
	$templateCode .= loadView('Eleitores-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: Eleitores_dv
	if(function_exists('Eleitores_dv')) {
		$args = [];
		Eleitores_dv(($hasSelectedId ? $selectedId : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}