function Pessoas_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Pessoas');
	if(!$arrPerm['insert']) return false;

	$data = [
		'Nome' => Request::val('Nome', ''),
		'Estado' => Request::lookup('Estado', ''),
		'Cidade' => Request::lookup('Cidade', ''),
		'Bairro' => Request::lookup('Bairro', ''),
		'Rua' => Request::val('Rua', ''),
		'numero' => Request::val('numero', ''),
		'CEP' => Request::val('CEP', ''),
		'Contato' => Request::val('Contato', ''),
		'profissao' => Request::lookup('profissao', ''),
		'Email' => Request::val('Email', ''),
		'Midias' => Request::val('Midias', ''),
	];

	if($data['Nome'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Nome': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['Contato'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Telefone': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}

	// hook: Pessoas_before_insert
	if(function_exists('Pessoas_before_insert')) {
		$args = [];
		if(!Pessoas_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Pessoas', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('Pessoas', $recID, calculated_fields()['Pessoas']);

	// hook: Pessoas_after_insert
	if(function_exists('Pessoas_after_insert')) {
		$res = sql("SELECT * FROM `Pessoas` WHERE `Id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!Pessoas_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	// record owner is current user
	$recordOwner = getLoggedMemberID();
	set_record_owner('Pessoas', $recID, $recordOwner);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) Pessoas_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

